import { Text, View, TextInput, TouchableOpacity, SafeAreaView, StyleSheet, AsyncStorage } from 'react-native';
import React, { useState } from 'react';

function Cadastrar({ navigation }) {
    const [nome, setNome] = useState('');
    const [cpf, setCpf] = useState('');
    const [dataNascimento, setDataNascimento] = useState('');
    const [endereco, setEndereco] = useState('');
    const [numero, setNumero] = useState('');
    const [bairro, setBairro] = useState('');
    const [telefone, setTelefone] = useState('');

    const handleEnviar = async () => {
        // Cria um objeto com os dados preenchidos
        const dados = {
            nome,
            cpf,
            dataNascimento,
            endereco,
            numero,
            bairro,
            telefone
        };

        try {
            // Salva os dados no AsyncStorage
            await AsyncStorage.setItem('dadosCadastro', JSON.stringify(dados));
            console.log('Dados de cadastro salvos com sucesso!');
            // Navega para a próxima tela após salvar os dados
            navigation.navigate('PróximaTela');
        } catch (error) {
            console.log('Erro ao salvar os dados de cadastro:', error);
        }
    };

    return (
        <SafeAreaView style={styles.container}>
            <View style={styles.container1}></View>
            <View style={styles.container2}>
                <TextInput
                    style={styles.input}
                    placeholder='Nome Completo'
                    placeholderTextColor='gray'
                    value={nome}
                    onChangeText={setNome}
                />
                <TextInput
                    style={styles.input}
                    placeholder='Nome Completo'
                    placeholderTextColor='gray'
                    value={cpf}
                    onChangeText={setCpf}
                />
                <TextInput
                    style={styles.input}
                    placeholder='Nome Completo'
                    placeholderTextColor='gray'
                    value={dataNascimento}
                    onChangeText={setDataNascimento}
                />
                <TextInput
                    style={styles.input}
                    placeholder='Nome Completo'
                    placeholderTextColor='gray'
                    value={endereco}
                    onChangeText={setNome}
                />
                <TextInput
                    style={styles.input}
                    placeholder='Nome Completo'
                    placeholderTextColor='gray'
                    value={bairro}
                    onChangeText={setNome}
                />
                <TextInput
                    style={styles.input}
                    placeholder='Nome Completo'
                    placeholderTextColor='gray'
                    value={telefone}
                    onChangeText={setNome}
                />
                
                <TouchableOpacity style={styles.bott} onPress={handleEnviar}>
                    <Text style={styles.txt}>ENVIAR</Text>
                </TouchableOpacity>
            </View>
            <View style={styles.container3}></View>
        </SafeAreaView>
    );
}

  
  
  const styles = StyleSheet.create({
    logo:{
        width:300,
        height:250,
        alignSelf:'center',
    },
    container: {
      flex: 1,
      backgroundColor:'#fff'
      // Defina os estilos desejados para o container aqui
    },

    container1:{
        backgroundColor:'#000',
        width:'100%',
        height:'7%',


    },
    container2:{
        width:'100%',
        height:'86%',
        paddingTop:20,


    },
    container3:{
        backgroundColor:'#000',
        width:'100%',
        height:'7%',


    },

    input:{
        marginTop:20,
        alignSelf:'center',
        width:'80%',
        height:55,
        borderRadius:10,
        borderWidth:1,
        paddingLeft:25,
        paddingRight:25,
        fontSize:20,
        fontStyle:'italic',


    },
    bott:{
        alignSelf:'center',
        
        alignItems:'center',
        justifyContent:'center',
        backgroundColor:'#000',
        width:'40%',
        height:65,
        borderRadius:50,
        marginTop:80,

    },
    txt:{
        color:'#FFF',
    },
  
  });
  export default Cadastrar;
