    // setConsultasMarcadas(retrievedConsultations);
  }, []);

  return (
    <SafeAreaView style={styles.container}>
      <ScrollView contentContainerStyle={styles.scrollContainer}>
        {/* ... */}
        <View style={styles.consultasContainer}>
          {consultasMarcadas.map((consulta, index) => (
            <TouchableOpacity
              style={styles.consultaItem}
              key={index}
              onPress={() => handleConsultaPress(consulta)}
            >
              <Text>Data: {consulta && consulta.data}</Text>
              <Text>Hora: {consulta && consulta.hora}</Text>
              <Text>Observação: {consulta && consulta.observacao}</Text>
