    },
    container2:{
        width:'100%',
        height:'86%',
        paddingTop:20,


    },
    container3:{
        backgroundColor:'#000',
        width:'100%',
        height:'7%',


    },

    input:{
        marginTop:20,
        alignSelf:'center',
        width:'80%',
        height:55,
        borderRadius:10,
        borderWidth:1,
        paddingLeft:25,
        fontSize:20,
        fontStyle:'italic',


    },
    bott:{
        alignSelf:'center',
        
        alignItems:'center',
        justifyContent:'center',
        backgroundColor:'#000',
        width:'40%',
        height:65,
        borderRadius:50,
        marginTop:100,

    },
    txt:{
        color:'#FFF',
    },
  
  });
  export default Cadastrar;
