import { Text, View, TextInput, TouchableOpacity, SafeAreaView, StyleSheet } from 'react-native';
import React from 'react';


function Clientes({ navigation }) {

    return(
        
        <SafeAreaView style ={styles.container}>

            <View style ={styles.container1}></View>
            <View style ={styles.container2}>

                <TouchableOpacity style ={styles.container4}>
                    <Text style ={styles.txt1}>Marcus Vinicius Lins Lemos Filho</Text>
                    <Text style ={styles.txt1}>CPF: 808.090.142-20</Text>
                    <Text style ={styles.txt1}>DATA NASCIMENTO</Text>
                    <Text style ={styles.txt4}>#1</Text>

                </TouchableOpacity>
             
            </View>
            <View style ={styles.container3}></View>


        </SafeAreaView>
               


      )
  
  }
  
  const styles = StyleSheet.create({
    logo:{
        width:300,
        height:250,
        alignSelf:'center',
    },
    container: {
      flex: 1,
      backgroundColor:'#fff'
      // Defina os estilos desejados para o container aqui
    },

    container1:{
        backgroundColor:'#000',
        width:'100%',
        height:'7%',


    },
    container2:{
        width:'100%',
        height:'86%',
        paddingTop:20,


    },
    container3:{
        backgroundColor:'#000',
        width:'100%',
        height:'7%',


    },

    // QUADRO 
    container4:{
        position:'relative',
        alignSelf:'center',
        backgroundColor:'#000',
        width:'90%',
        height:180,
        borderRadius:10,
        padding:10,
    },

    txt1:{
        color:'#fff',
        fontSize:20,
        marginTop:9,

    },

    txt4:{
        position:'absolute',
        color:'#fff',
        fontSize:30,
        right:20,

    },
  
  });
  export default Clientes;
