import { Text, View, SafeAreaView, TouchableOpacity, StyleSheet, Image } from 'react-native';
import React, { useEffect, useState } from 'react';

function ClienteProfile({ route }) {
  const { cliente } = route.params;
  const [photo, setPhoto] = useState(null);

  useEffect(() => {
    console.log('Dados do cliente:', cliente);
  }, []);

  const handleAddSelfie = () => {
    // Aqui você pode adicionar a lógica para capturar a selfie do usuário
    // e salvar em algum lugar, como no estado 'photo'
    // Exemplo:
    // const selfie = captureSelfie();
    // setPhoto(selfie);
  };

  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.container1}></View>
      <View style={styles.container2}>
        <TouchableOpacity style={styles.photoContainer} onPress={handleAddSelfie}>
          {photo ? (
            <Image source={photo} style={styles.photo} />
          ) : (
            <Text style={styles.addSelfieText}>Clique para adicionar uma selfie</Text>
          )}
        </TouchableOpacity>
        {Object.entries(cliente).map(([key, value]) => (
          <Text key={key}>
            {key}: {value}
          </Text>
        ))}
      </View>
      <View style={styles.container3}></View>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  logo: {
    width: 300,
    height: 250,
    alignSelf: 'center',
  },
  container: {
    flex: 1,
    backgroundColor: '#fff',
  },
  container1: {
    backgroundColor: '#000',
    width: '100%',
    height: '7%',
  },
  container2: {
    backgroundColor: '#FFF',
    width: '100%',
    height: '86%',
    paddingTop: 20,
    alignItems: 'center',
    justifyContent: 'center',
  },
  container3: {
    backgroundColor: '#000',
    width: '100%',
    height: '7%',
  },
  photoContainer: {
    width: 150,
    height: 150,
    borderRadius: 75,
    backgroundColor: '#EEE',
    alignItems: 'center',
    justifyContent: 'center',
    marginBottom: 20,
  },
  photo: {
    width: 150,
    height: 150,
    borderRadius: 75,
  },
  addSelfieText: {
    fontSize: 16,
    color: '#888',
  },
});

export default ClienteProfile;
