// MinhasConsultas.js

import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet, FlatList } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';

function MinhasConsultas() {
  const [consultas, setConsultas] = useState([]);
  const [cpf, setCpf] = useState('');
  const [nomeCliente, setNomeCliente] = useState('');
  const [cpfCliente, setCpfCliente] = useState('');
  const [dataNascimentoCliente, setDataNascimentoCliente] = useState('');

  const carregarConsultas = async () => {
    try {
      const consultasSalvas = await AsyncStorage.getItem('Consultas');
      if (consultasSalvas !== null) {
        const consultasParseadas = JSON.parse(consultasSalvas);
        const consultasFiltradas = consultasParseadas.filter(
          (consulta) => consulta.clienteCPF === cpf
        );
        setConsultas(consultasFiltradas);
      }
    } catch (error) {
      console.log('Erro ao carregar consultas:', error);
    }
  };

  const carregarCadastro = async () => {
    try {
      const cadastroSalvo = await AsyncStorage.getItem('Cadastro');
      if (cadastroSalvo !== null) {
        const cadastroParseado = JSON.parse(cadastroSalvo);
        setCpf(cadastroParseado.cpf);
        setNomeCliente(cadastroParseado.nome);
        setCpfCliente(cadastroParseado.cpf);
        setDataNascimentoCliente(cadastroParseado.dataNascimento);
      }
    } catch (error) {
      console.log('Erro ao carregar informações do cadastro:', error);
    }
  };

  useEffect(() => {
    carregarConsultas();
    carregarCadastro();
  }, []);

  const renderItem = ({ item }) => (
    <View style={styles.item}>
      <View style={styles.clienteContainer}>
        <Text style={styles.label}>Nome do Cliente:</Text>
        <Text>{item.clienteNome ? item.clienteNome : 'Nome não fornecido'}</Text>
        <Text style={styles.label}>CPF:</Text>
        <Text>{item.clienteCPF ? item.clienteCPF : 'CPF não fornecido'}</Text>
        <Text style={styles.label}>Data de Nascimento:</Text>
        <Text>
          {item.clienteDataNascimento ? item.clienteDataNascimento : 'Data de nascimento não fornecida'}
        </Text>
      </View>
      <View style={styles.consultaContainer}>
        <Text style={styles.label}>Horário:</Text>
        <Text>{item.horaConsulta}</Text>
        <Text style={styles.label}>Observação:</Text>
        <Text>{item.observacao}</Text>
        <Text style={styles.label}>Data da Consulta:</Text>
        <Text>{item.dataConsulta}</Text>
      </View>
    </View>
  );

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Minhas Consultas</Text>
      {consultas.length > 0 ? (
        <FlatList
          data={consultas}
          renderItem={renderItem}
          keyExtractor={(item, index) => `consulta_${index}`}
          showsVerticalScrollIndicator={false}
        />
      ) : (
        <Text style={styles.noConsultasText}>Nenhuma consulta marcada.</Text>
      )}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
    justifyContent: 'center',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 16,
  },
  item: {
    marginBottom: 16,
    padding: 16,
    borderRadius: 8,
    backgroundColor: '#f2f2f2',
  },
  clienteContainer: {
    marginBottom: 8,
  },
  consultaContainer: {},
  label: {
    fontWeight: 'bold',
  },
  noConsultasText: {
    fontSize: 16,
    textAlign: 'center',
  },
});

export default MinhasConsultas;
