import { StyleSheet } from "react-native";

const styles = StyleSheet.create({


        bola:{
            height:200,
            width:200,
            borderRadius:400,
            backgroundColor:'#fff',

        },

        bg:{

            flex:1,
            justifyContent:'flex-end',
            width:'100%',
            height:'100%',
            
        },
      
        CaixaLogin:{
            
            padding:50,

            width:'100%',
            height:'65%',
            backgroundColor:'#FFF',

            shadowColor: '#000',
            shadowOffset: { width: 0, height: -10 },
            shadowOpacity: 0.3,
            shadowRadius: 20,
            elevation: 2, // Esta propriedade é específica do Android

        },
        caixa:{
            height:'100%',
            width:'100%',            
        },
        
       
        nome:{
            fontSize: 20,
            marginLeft: 15,
        },

        Login:{
            marginTop:'15%',

        },

        inp:{
            marginTop:'4%',
            backgroundColor:'#fff',
            borderWidth:1,
            borderColor:'#F0F0F0',
            borderRadius:30,

            width:'100%',
            height: 70,

            textAlign:'center',
            textAlignVertical:'center',

        },
        txtsenha:{
            color:'#559DED',

        },
        containertxt:{
            marginTop:10,
            marginRight:20,
            flexDirection:'row',
            alignSelf:'flex-end'

        },

        butt:{
            alignSelf:'center',

            width:'70%',
            height: 55,

            marginTop:'2%',

            borderRadius:30,

            textAlign:'center',
            textAlignVertical:'center',

            backgroundColor:'#01CCAA',


        },
        
        txtbutt:{
            color:'#fff',
            fontSize:35,
        }

  });

  export default styles;
