import { View, ScrollView, SafeAreaView, StyleSheet } from 'react-native';
import Cardclientes from '../Components/Home/Cardclientes';
import React, { useState, useEffect } from 'react';
import AsyncStorage from '@react-native-async-storage/async-storage';

function Clientes({ navigation }) {
  const [clientes, setClientes] = useState([]);

  useEffect(() => {
    carregarClientes();
  }, []);

  const carregarClientes = async () => {
    try {
      // Obter os clientes cadastrados do AsyncStorage
      const dadosClientes = await AsyncStorage.getItem('Clientes');
      if (dadosClientes !== null) {
        // Converter os dados de volta para um array de objetos
        const clientesSalvos = JSON.parse(dadosClientes);
        setClientes(clientesSalvos);
      }
    } catch (error) {
      console.log('Erro ao carregar os clientes:', error);
    }
  };

  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.container1}></View>
      <ScrollView style={styles.container2}>
        {clientes.map((cliente, index) => (
          <Cardclientes
            key={index}
            Nome={cliente.nome}
            CPF={cliente.cpf}
            Date={cliente.dataNascimento}
          />
        ))}
      </ScrollView>
      <View style={styles.container3}></View>
    </SafeAreaView>
  );
}

  const styles = StyleSheet.create({
    logo:{
        width:300,
        height:250,
        alignSelf:'center',
    },
    container: {
      flex: 1,
      backgroundColor:'#fff'
      // Defina os estilos desejados para o container aqui
    },


    container1:{
        backgroundColor:'#000',
        width:'100%',
        height:'7%',
        top:-60,

    },
    container2:{
        width:'100%',
        height:'86%',


    },
    container3:{
        backgroundColor:'#000',
        width:'100%',
        height:'7%',
        bottom:-40,


    },

    // QUADRO 
    container4:{
        position:'relative',
        alignSelf:'center',
        backgroundColor:'#000',
        width:'90%',
        height:180,
        borderRadius:10,
        paddingLeft:20,
        paddingTop:20,
    },

    txt1:{
        color:'#fff',
        fontSize:15,
        marginTop:3,

    },

    txt4:{
        position:'absolute',
        color:'#fff',
        fontSize:30,
        right:20,
        top:130,

    },
  
  });
  export default Clientes;
