import React, { useEffect, useState } from 'react';
import { Text, View, SafeAreaView, StyleSheet, ScrollView, TouchableOpacity } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';

function ClienteProfile({ route, navigation }) {
  const { cliente } = route.params;
  const [consultasMarcadas, setConsultasMarcadas] = useState([]);

  useEffect(() => {
    fetchConsultasMarcadas();
  }, []);

  const fetchConsultasMarcadas = async () => {
    try {
      const consultasSalvas = await AsyncStorage.getItem('Consultas');

      if (consultasSalvas !== null) {
        const consultas = JSON.parse(consultasSalvas);
        setConsultasMarcadas(consultas);
      }
    } catch (error) {
      console.log('Erro ao buscar as consultas marcadas:', error);
    }
  };
  useEffect(() => {
    console.log('Dados do cliente:', cliente);
  }, []);

  const handleNavigateToPage = () => {
    console.log('Chamando handleNavigateToPage');
    console.log('Cliente:', cliente);

    navigation.navigate('MarcarConsultas', {
      cliente,
      adicionarConsultaId: adicionarConsultaId,
      adicionarConsulta: adicionarConsultaMarcada,
    });
  };

  const adicionarConsultaId = (consultaId) => {
    console.log('ID da consulta:', consultaId);
  };

  const adicionarConsultaMarcada = (consulta) => {
    setConsultasMarcadas((prevConsultas) => [...prevConsultas, consulta]);
  };

  const handleConsultaPress = (consulta) => {
    console.log('Consulta pressionada:', consulta);
  };

  return (
    <SafeAreaView style={styles.container}>
      <ScrollView contentContainerStyle={styles.scrollContainer}>
        <View style={styles.clienteInfoContainer}>
          {/* Código das informações do cliente */}
        </View>
        <View style={styles.consultasContainer}>
          <Text style={styles.consultasTitle}>Consultas Marcadas:</Text>
          {consultasMarcadas.map((consulta, index) => (
            <View style={styles.consultaCard} key={index}>
              <Text style={styles.consultaData}>{consulta.data}</Text>
              <Text style={styles.consultaHora}>{consulta.hora}</Text>
              <Text style={styles.consultaObservacao}>{consulta.observacao}</Text>
            </View>
          ))}
        </View>
      </ScrollView>
    </SafeAreaView>
  );
}


const styles = StyleSheet.create({
  container: {
    paddingTop:24,
    flex: 1,
    backgroundColor: '#fff',
  },
  consultasContainer: {
    flexGrow: 1,
    alignItems: 'center',
    marginTop: 20,
  },
  scrollContainer: {
    
    paddingTop:15,
    flexGrow: 1,
    alignItems: 'center',
    paddingTop: 20,
  },
  clienteInfoContainer: {
    width: '80%',
  },
  clienteInfoRow: {
    flexDirection: 'row',
    marginBottom: 10,
  },
  clienteInfoBox: {
    flex: 1,
    backgroundColor: '#F5F5F5',
    padding: 10,
    marginRight: 10,
    borderRadius: 5,
  },
  clienteInfoKey: {
    fontWeight: 'bold',
    color: '#333',
  },
  clienteInfoValue: {
    color: '#555',
  },
  footerButton: {
    backgroundColor: '#000',
    alignItems: 'center',
    justifyContent: 'center',
    height: 50,
  },
  footerButtonText: {
    color: '#FFF',
    fontSize: 16,
  },
  consultasContainer: {
    flexGrow: 1,
    alignItems: 'center',
    marginTop: 20,
  },
  consultaItem: {
    backgroundColor: '#F5F5F5',
    padding: 10,
    marginVertical: 5,
    borderRadius: 5,
  },
});

export default ClienteProfile;
