import { StyleSheet } from "react-native";

const styles = StyleSheet.create({

        bg:{

            flex:1,
            justifyContent:'flex-end',
            
        },
      
        CaixaLogin:{

            padding:50,

            width:'100%',
            height:590,
            backgroundColor:'#fff',

            shadowColor: '#000',
            shadowOffset: { width: 0, height: -10 },
            shadowOpacity: 0.3,
            shadowRadius: 20,
            elevation: 2, // Esta propriedade é específica do Android

        },
        caixa:{

            height:'100%',
            width:'100%',

            
        },
        
       
        nome:{

            fontSize: 20,
            marginLeft: 15,


        },

        Login:{
            marginTop:'15%',

        },

        inp:{
            marginTop:'4%',
            backgroundColor:'#fff',
            borderWidth:'1',
            borderColor:'gray',
            borderRadius:30,

            width:'100%',
            height: 70,


        },
        

  });

  export default styles;
