import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet, FlatList } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';

function MinhasConsultas({ cpf }) {
  const [consultas, setConsultas] = useState([]);
  useEffect(() => {
    const carregarConsultas = async () => {
      try {
        const consultasSalvas = await AsyncStorage.getItem('Consultas');
        if (consultasSalvas !== null) {
          const consultasParseadas = JSON.parse(consultasSalvas);
          const consultasFiltradas = consultasParseadas.filter(
            (consulta) => consulta.clienteCPF === cpf
          );
          setConsultas(consultasFiltradas);
        }
        
        const carregarCliente = async () => {
          try {
            const clienteSalvo = await AsyncStorage.getItem('Cliente');
            if (clienteSalvo !== null) {
              const clienteParseado = JSON.parse(clienteSalvo);
              setNomeCliente(clienteParseado.nome);
              setCpfCliente(clienteParseado.cpf);
              setDataNascimentoCliente(clienteParseado.dataNascimento);
            }
          } catch (error) {
            console.log('Erro ao carregar informações do cliente:', error);
          }
        };
        
        carregarCliente();

        // Buscar informações do cliente
        const clienteSalvo = await AsyncStorage.getItem('Cliente');
        if (clienteSalvo !== null) {
          const clienteParseado = JSON.parse(clienteSalvo);
          setNomeCliente(clienteParseado.nome);
          setCpfCliente(clienteParseado.cpf);
          setDataNascimentoCliente(clienteParseado.dataNascimento);
        }
      } catch (error) {
        console.log('Erro ao carregar consultas:', error);
      }
    };

    carregarConsultas();
  }, [cpf]);

  const renderItem = ({ item }) => (
    <View style={styles.item}>
      <View style={styles.clienteContainer}>
        <Text style={styles.label}>Nome do Cliente:</Text>
        <Text>{nomeCliente ? nomeCliente : 'Nome não fornecido'}</Text>
        <Text style={styles.label}>CPF:</Text>
        <Text>{cpfCliente ? cpfCliente : 'CPF não fornecido'}</Text>
        <Text style={styles.label}>Data de Nascimento:</Text>
        <Text>{dataNascimentoCliente ? dataNascimentoCliente : 'Data de nascimento não fornecida'}</Text>
      </View>
      <View style={styles.consultaContainer}>
        <Text style={styles.label}>Horário:</Text>
        <Text>{item.horaConsulta}</Text>
        <Text style={styles.label}>Observação:</Text>
        <Text>{item.observacao}</Text>
        <Text style={styles.label}>Data da Consulta:</Text>
        <Text>{item.dataConsulta}</Text>
      </View>
    </View>
  );

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Minhas Consultas</Text>
      {consultas.length > 0 ? (
        <FlatList
          data={consultas}
          renderItem={renderItem}
          keyExtractor={(item, index) => `consulta_${index}`}
          showsVerticalScrollIndicator={false}
        />
      ) : (
        <Text style={styles.noConsultasText}>Nenhuma consulta marcada.</Text>
      )}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
    justifyContent: 'center',
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 16,
  },
  item: {
    marginBottom: 24,
    backgroundColor: '#c2c5cc',
    padding: 16,
    borderRadius: 8,
  },
  clienteContainer: {
    marginBottom: 8,
  },
  consultaContainer: {
    marginTop: 8,
  },
  label: {
    fontWeight: 'bold',
    marginBottom: 4,
  },
  noConsultasText: {
    marginTop: 24,
    textAlign: 'center',
  },
});

export default MinhasConsultas;
