import { Text, View, SafeAreaView, TouchableOpacity, StyleSheet, Image, ScrollView } from 'react-native';
import React, { useEffect, useState } from 'react';

function ClienteProfile({ route }) {
  const { cliente } = route.params;
  const [photo, setPhoto] = useState(null);

  useEffect(() => {
    console.log('Dados do cliente:', cliente);
  }, []);

  const handleAddSelfie = () => {
    // Aqui você pode adicionar a lógica para capturar a selfie do usuário
    // e salvar em algum lugar, como no estado 'photo'
    // Exemplo:
    // const selfie = captureSelfie();
    // setPhoto(selfie);
  };

  return (
    <SafeAreaView style={styles.container}>
      <ScrollView contentContainerStyle={styles.scrollContainer}>
        <TouchableOpacity style={styles.photoContainer} onPress={handleAddSelfie}>
          {photo ? (
            <Image source={photo} style={styles.photo} />
          ) : (
            <Text style={styles.addSelfieText}>Clique para adicionar uma selfie</Text>
          )}
        </TouchableOpacity>
        <View style={styles.clienteInfoContainer}>
          {Object.entries(cliente).map(([key, value]) => (
            <View style={styles.clienteInfoRow} key={key}>
              <Text style={styles.clienteInfoKey}>{key}:</Text>
              <Text style={styles.clienteInfoValue}>{value}</Text>
            </View>
          ))}
        </View>
      </ScrollView>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
  },
  scrollContainer: {
    flexGrow: 1,
    alignItems: 'center',
    paddingTop: 20,
  },
  photoContainer: {
    width: 150,
    height: 150,
    borderRadius: 75,
    backgroundColor: '#EEE',
    alignItems: 'center',
    justifyContent: 'center',
    marginBottom: 20,
  },
  photo: {
    width: 150,
    height: 150,
    borderRadius: 75,
  },
  addSelfieText: {
    fontSize: 16,
    color: '#888',
  },
  clienteInfoContainer: {
    width: '80%',
  },
  clienteInfoRow: {
    flexDirection: 'row',
    marginBottom: 10,
  },
  clienteInfoKey: {
    fontWeight: 'bold',
    marginRight: 10,
  },
  clienteInfoValue: {
    flex: 1,
  },
});

export default ClienteProfile;
