import { StyleSheet } from "react-native";
import { Platform } from "react-native-web";

const styles = StyleSheet.create({

    container: {

      flex:1,
    },   

    containerh:{
        width:'100%',
        height:'5%',
        backgroundColor:'#01CCAA',


    },

    Idade:{
        flexDirection:'row',

    },
    prompt:{
        flex:1,
        width:'100%',
        height:'90%',
        backgroundColor: '#0ff',
        paddingTop:50,
        padding:'10%',

        fontSize: Platform.Os === 'ios'? 20 : 20,

    },

    containerf:{
        width:'100%',
        height:'5%',
        backgroundColor:'#01CCAA',
        
    },
    scrollcli:{
        backgroundColor:'#0f3',

    },

    Cards:{
        width:'100%',
        height:140,
        backgroundColor:'#01CCAA',
        borderRadius:40,

        paddingTop:'5%',
        paddingLeft:'10%',


    },
});

  export default styles;
