import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet, TextInput, TouchableOpacity, ScrollView, Linking } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';

function MarcarConsultas({ route, navigation }) {
  const { cliente, adicionarConsultaId, adicionarConsulta } = route.params || {};
  const [dataConsulta, setDataConsulta] = useState('');
  const [horaConsulta, setHoraConsulta] = useState('');
  const [observacao, setObservacao] = useState('');

  const handleSalvarConsulta = async () => {
    const consulta = {
      data: dataConsulta,
      hora: horaConsulta,
      observacao: observacao,
    };

    // Salva a consulta no AsyncStorage
    try {
      const consultasSalvas = await AsyncStorage.getItem('Consultas');

      let consultas = [];
      if (consultasSalvas !== null) {
        consultas = JSON.parse(consultasSalvas);
      }

      consultas.push(consulta);

      await AsyncStorage.setItem('Consultas', JSON.stringify(consultas));
    } catch (error) {
      console.log('Erro ao salvar as consultas:', error);
    }

    // Chama a função adicionarConsulta para atualizar as consultas em ClienteProfile
    adicionarConsulta(consulta);

    navigation.goBack();
  };

  useEffect(() => {
    navigation.setOptions({
      headerRight: () => (
        <TouchableOpacity onPress={handleSalvarConsulta}>
          <Text style={styles.headerButtonText}>Salvar</Text>
        </TouchableOpacity>
      ),
    });
  }, [navigation]);

  return (
    <View style={styles.container}>
      <View style={styles.laudoContainer}>
        {/* Código do laudo do cliente */}
      </View>
      <ScrollView style={styles.scrollContainer}>
        <View style={styles.formContainer}>
          {/* Código do formulário */}
        </View>
      </ScrollView>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    paddingTop: '15%',
    flex: 1,
    backgroundColor: '#fff',
  },
  laudoContainer: {
    paddingHorizontal: 20,
    paddingTop: 20,
  },
  laudoTitle: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  laudoText: {
    fontSize: 16,
    marginBottom: 5,
  },
  scrollContainer: {
    flex: 1,
    marginTop: 20,
    paddingHorizontal: 20,
  },
  formContainer: {
    marginBottom: 20,
  },
  label: {
    fontSize: 16,
    fontWeight: 'bold',
    marginBottom: 5,
  },
  input: {
    height: 40,
    borderColor: '#ccc',
    borderWidth: 1,
    borderRadius: 5,
    paddingHorizontal: 10,
    marginBottom: 10,
  },
  button: {
    backgroundColor: '#000',
    alignItems: 'center',
    justifyContent: 'center',
    height: 40,
    borderRadius: 5,
  },
  buttonText: {
    color: '#fff',
    fontSize: 16,
  },
  headerButtonText: {
    color: '#000',
    fontSize: 16,
    marginRight: 10,
  },
});

export default MarcarConsultas;
