import { StyleSheet } from "react-native";

const styles = StyleSheet.create({
  bg: {
    alignItems: 'center',
  },
  fundo: {
    marginTop: -350,
    width: 800,
    height: 800,
    borderRadius: '100%',
    backgroundColor: '#Ef2',
    overflow: 'hidden', // Para recortar a sombra que se estenderia além do contêiner

    // Estilos para o elemento que representa a sombra interna
    justifyContent: 'center',
    alignItems: 'center',
  },
  innerShadow: {
    width: 400,
    height: 400,
    shadowColor: 'black',
    shadowOffset: { width: 0, height: 0 },
    shadowOpacity: 1,
    shadowRadius: 10,
  },
});
