import { Text, View, TextInput, TouchableOpacity, Image, SafeAreaView, StyleSheet } from 'react-native';
import React from 'react';


function Home({ navigation }) {

    const logo = require('../../assets/logo.png')
    return(
        
        <SafeAreaView style ={styles.container}>

            <View style ={styles.container1}></View>
            <View style ={styles.container2}>

                <Image source={logo} style ={styles.logo} />
                <View style ={styles.botaohome}>
                    <TouchableOpacity style ={styles.bott}>
                        <Text style ={styles.txt}>LOGIN</Text>
                    </TouchableOpacity>
                    <TouchableOpacity style ={styles.bott}>
                        <Text style ={styles.txt}>LOGIN</Text>
                    </TouchableOpacity>
                </View>
           
           </View>
            <View style ={styles.container3}></View>


        </SafeAreaView>
               


      )
  
  }
  
  const styles = StyleSheet.create({
    logo:{
        width:200,
        height:150,
        alignSelf:'center',
    },
    container: {
      flex: 1,
      backgroundColor:'#fff'
      // Defina os estilos desejados para o container aqui
    },

    container1:{
        backgroundColor:'#000',
        width:'100%',
        height:'7%',


    },
    container2:{
        width:'100%',
        height:'86%',


    },
    container3:{
        backgroundColor:'#000',
        width:'100%',
        height:'7%',


    },

    // botao
    botaohome:{
        flexDirection:'row',
        justifyContent:'space-around',

    },
    bott:{
        alignSelf:'center',
        
        alignItems:'center',
        justifyContent:'center',
        backgroundColor:'#000',
        width:150,
        height:150,
        borderRadius:10,
        marginTop:100,

    },
    txt:{
        color:'#FFF',
    },
  
  });
  export default Home;
