import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet, FlatList } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';

function MinhasConsultas() {
  const [consultas, setConsultas] = useState([]);

  useEffect(() => {
    carregarConsultas();
  }, []);

  const carregarConsultas = async () => {
    try {
      const consultasSalvas = await AsyncStorage.getItem('Consultas');
      if (consultasSalvas !== null) {
        const consultasParseadas = JSON.parse(consultasSalvas);
        setConsultas(consultasParseadas);
      }
    } catch (error) {
      console.log('Erro ao carregar consultas:', error);
    }
  };

  const renderItem = ({ item }) => (
    <View style={styles.item}>
      <Text>Data da Consulta: {item.dataConsulta}</Text>
      <Text>Hora da Consulta: {item.horaConsulta}</Text>
      <Text>Observação: {item.observacao}</Text>
    </View>
  );

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Minhas Consultas</Text>
      {consultas.length > 0 ? (
        <FlatList
          data={consultas}
          renderItem={renderItem}
          keyExtractor={(item) => item.clienteId.toString()}
        />
      ) : (
        <Text>Nenhuma consulta marcada.</Text>
      )}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 16,
  },
  item: {
    marginBottom: 8,
  },
});

export default MinhasConsultas;
