import React from 'react';
import { View, Text, TouchableOpacity, SafeAreaView, StyleSheet } from 'react-native';
import Cardclientes from '../Components/Home/Cardclientes';

function ClienteProfile({ navigation }) {
  // Dados de exemplo do cliente
  const cliente = {
    nome: 'Nome do Cliente',
    cpf: '123.456.789-00',
    dataNascimento: '01/01/1990',
  };

  const handleMarcarConsulta = () => {
    // Implemente a ação de marcar consulta aqui
    console.log('Consulta marcada!');
  };

  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.container1}></View>
      <View style={styles.container2}>
        <Cardclientes
          Nome={cliente.nome}
          CPF={cliente.cpf}
          Date={cliente.dataNascimento}
        />

        {/* Adicione a imagem do cliente aqui */}
        {/* Substitua a linha abaixo pelo componente de imagem do cliente */}
        <Text style={styles.text}>Imagem do Cliente</Text>

        <TouchableOpacity style={styles.button} onPress={handleMarcarConsulta}>
          <Text style={styles.buttonText}>Marcar Consulta</Text>
        </TouchableOpacity>
      </View>
      <View style={styles.container3}></View>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
  },
  container1: {
    backgroundColor: '#000',
    width: '100%',
    height: '7%',
  },
  container2: {
    backgroundColor: '#030',
    width: '100%',
    height: '86%',
    paddingTop: 20,
    alignItems: 'center',
  },
  container3: {
    backgroundColor: '#000',
    width: '100%',
    height: '7%',
  },
  text: {
    fontSize: 16,
    fontWeight: 'bold',
    color: '#333',
    marginBottom: 20,
  },
  button: {
    backgroundColor: '#000',
    width: 200,
    height: 50,
    borderRadius: 25,
    justifyContent: 'center',
    alignItems: 'center',
    marginTop: 20,
  },
  buttonText: {
    color: '#fff',
    fontSize: 16,
    fontWeight: 'bold',
  },
});

export default ClienteProfile;
